definition.script:
node("slave"){
    stage ('Check')
{
git 'https://github.com/spring-guides/gs-spring-boot.git'
}
stage ('build')
{
    dir('initial/')
{
    sh '''
    alias gradle='sudo docker run --rm -v $(pwd):$(pwd) -w $(pwd) docker_gradle gradle'
    gradle build
    sudo mv build/libs/gs-spring-boot-0.1.0.jar /home/student/Docker/repo/docker/spring-boot_application.jar
    '''
}
}
}
